import React, { Component } from "react";
import { Route, Switch, Redirect } from "react-router-dom";
import Home from "./Components/Home";
import Navbars from "./layouts/Navbar";
import PageNotFound from "./Components/404";

import SingleMoviesPage from "./layouts/SingleMoviesPage";
import Banner from "./layouts/Banner";
import "bootstrap/dist/css/bootstrap.min.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      movie: {}
    };
  }
  handleRecMovies = movie => {
    this.setState({ movie });
  };
  render() {
    return (
      <React.Fragment>
        <Navbars title="" />
        <Switch>
          <Route
            path="/"
            exact
            render={props => (
              <Home {...props} moviesData={this.handleRecMovies} />
            )}
          />

          <Route
            path="/movies"
            render={props => (
              <SingleMoviesPage {...props} movie={this.state.movie} />
            )}
          />
          <Route path="/banner" component={Banner} />
          <Route path="/pagenot" component={PageNotFound} />
          <Redirect to="/pagenot" />
        </Switch>
      </React.Fragment>
    );
  }
}

export default App;
