const initialState = {
  count: 0
};
const counterReducer = (state = initialState, action) => {
  switch (action.type) {
    case "SET_COUNTER":
      return {
        ...state,
        count: action.payload
      };
    default:
      return state;
  }
};
export default counterReducer;
