import React, { Component } from "react";
import styled from "styled-components";
import { Container, Card, Row, Col } from "react-bootstrap";
import Axios from "axios";
import footer from "../assets/images/footer.jpg";
import { Link } from "react-router-dom";

class PopularMovies extends Component {
  constructor() {
    super();
    this.state = {
      movies: [],
      url: `https://api.themoviedb.org/3/movie/popular?api_key=74414556191f067bc88a8b0b6338cdda&language=en-US&page=1`
    };
  }
  componentDidMount() {
    //get request
    //post request
    //delete request
    //put or patch request
    Axios.get(this.state.url).then(res =>
      this.setState({ movies: res.data.results })
    );
  }
  render() {
    return (
      <PopularWrapper>
        <h1>Popular Movies</h1>
        <Container>
          <Row>
            {this.state.movies.map((movie, index) => {
              if (index < 6) {
                return (
                  <Col md="3" onClick={() => this.props.moviesData(movie)}>
                    <Link to={`/Movies/${movie.id}`}>
                      <Card
                        style={{
                          width: "17.5rem",

                          border: "1px solid transparent",
                          boxShadow:
                            "0 10px 20px rgba(0,0,0,0.80), 0 6px 6px rgba(0,0,0,0.50)",
                          borderRadius: "5px",
                          margin: "5px",
                          color: "#23ff09",
                          backgroundColor: "transparent"
                        }}
                      >
                        <Card.Img
                          variant="top"
                          src={`https://image.tmdb.org/t/p/w500/${movie.backdrop_path}`}
                        />
                        <Card.Body>
                          <Card.Title>
                            {movie.original_title}
                            <br />
                            {`Release Date=${movie.release_date}`}
                          </Card.Title>
                        </Card.Body>
                      </Card>
                    </Link>
                  </Col>
                );
              }
            })}
          </Row>
        </Container>
      </PopularWrapper>
    );
  }
}
const PopularWrapper = styled.div`
  background-image: url(${footer});

  background-size: cover;
  position: relative;
  display: block;

  color: #6ff;
  font-weight: 700;
  a:hover{
    text-decoration: none;
      color: blue;
      background-color: orange;
  }
`;

export default PopularMovies;
